const BUTTON=document.getElementById("button"),RECORD=document.getElementById("record"),TIMER=document.getElementById("timer"),SCORE=document.getElementById("score"),MAXTIME=5e3,TICK=10;let secTime=0,time=5e3,scoreCount=0,record=0;function gameInit(){startTimer(),BUTTON.onclick=gameProcess}function startTimer(){setTimeout(gameEnd,5e3);let e=setInterval(()=>{time<=0?clearInterval(e):(time-=TICK,TIMER.textContent="Время: "+(time/1e3).toFixed(2))},TICK)}function gameProcess(){scoreCount++,SCORE.textContent="Счёт: "+scoreCount}function gameEnd(){BUTTON.setAttribute("disabled",!0)}BUTTON.onclick=(()=>{gameInit()});